import React from 'react';
import {
  Avatar,
  Card,
  CardContent,
  CardHeader,
  Chip,
  Stack,
  Typography,
} from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

const UserStoryPage = () => {
  return (
    <Stack spacing={3} sx={{ padding: 3 }}>
      <Card>
        <CardHeader
          title="User Story Title"
          subheader="Assigned to: AI Team"
          avatar={
            <Avatar sx={{ bgcolor: 'primary.main' }}>
              <PersonIcon />
            </Avatar>
          }
        />
        <CardContent>
          <Typography variant="h6" gutterBottom>
            Description
          </Typography>
          <Typography variant="body1" paragraph>
            This is a detailed description of the AI-generated user story. It explains the goals, requirements, and acceptance criteria.
          </Typography>
          <Typography variant="h6" gutterBottom>
            Status
          </Typography>
          <Chip icon={<CheckCircleIcon />} label="In Progress" color="warning" />
        </CardContent>
      </Card>
    </Stack>
  );
};

export default UserStoryPage;
