import React from 'react';
import {
  Avatar,
  Card,
  CardContent,
  CardHeader,
  Chip,
  Stack,
  Typography,
} from '@mui/material';
import ListAltIcon from '@mui/icons-material/ListAlt';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

const EpicsPage = () => {
  // Example data for epics
  const epics = [
    {
      id: 1,
      title: 'Epic 1',
      description: 'This is the description for Epic 1.',
      status: 'In Progress',
    },
    {
      id: 2,
      title: 'Epic 2',
      description: 'This is the description for Epic 2.',
      status: 'Pending',
    },
    {
      id: 3,
      title: 'Epic 3',
      description: 'This is the description for Epic 3.',
      status: 'Completed',
    },
  ];

  return (
    <Stack spacing={3} sx={{ padding: 3 }}>
      {epics.map((epic) => (
        <Card key={epic.id}>
          <CardHeader
            title={epic.title}
            subheader={`Status: ${epic.status}`}
            avatar={
              <Avatar sx={{ bgcolor: 'primary.main' }}>
                <ListAltIcon />
              </Avatar>
            }
          />
          <CardContent>
            <Typography variant="body1" paragraph>
              {epic.description}
            </Typography>
            <Typography variant="h6" gutterBottom>
              Status
            </Typography>
            <Chip
              icon={<CheckCircleIcon />}
              label={epic.status}
              color={epic.status === 'Completed' ? 'success' : 'warning'}
            />
          </CardContent>
        </Card>
      ))}
    </Stack>
  );
};

export default EpicsPage;
