import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import Login from "./pages/Login";
import Main from "./pages/waa";
import Sprints from "./pages/Sprints";
import Inbox from "./pages/Inbox";
import Bugs from "./pages/Bugs";
import DrawerNavigation from "./components/DrawerNavigation";
import { Box, Toolbar } from "@mui/material";
import BackLog from "./pages/BackLog";
import BackLogTemplate from "./Templates/BackLog";
import BugsTemplate from "./Templates/Bugs";
import SprintsTemplate from "./Templates/Sprints";
import DocsTemplate from "./Templates/Docs";
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  console.log(isLoggedIn);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  return (
    <Router>
      {isLoggedIn ? (
        <>
          <Box sx={{ display: "flex" }}>
            <DrawerNavigation />
            <Box
              component="main"
              sx={{
                flexGrow: 1,
                p: 2,
              }}
            >
              <Toolbar />
              {/* Private Routes */}
              <Routes>
                {/* default pages routes */}
                <Route path="/" element={<Navigate to="/main" replace />} />
                <Route
                  path="/main"
                  element={<Main onLogout={handleLogout} />}
                />
                <Route path="/inbox" element={<Inbox />} />
                <Route path="/sprints" element={<Sprints />} />
                <Route path="/bugs" element={<Bugs />} />
                <Route path="/backlog" element={<BackLog />} />
                {/*default template routes */}
                <Route path="/template/backlog" element={<BackLogTemplate />} />
                <Route path="/template/docs" element={<DocsTemplate />} />
                <Route path="/template/sprints" element={<SprintsTemplate />} />
                <Route path="/template/bugs" element={<BugsTemplate />} />
              </Routes>
            </Box>
          </Box>
        </>
      ) : (
        <Routes>
          {/* Public Routes */}
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          {/* Redirect to login if not logged in */}
          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      )}
    </Router>
  );
}

export default App;
