// ProfilePage.jsx

import React, { useState } from "react";
import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from "@mui/material";
import { makeStyles } from "@mui/styles";
import NewProfileForm from "./NewProfileForm"; // Importing the new profile form component

// Sample data for profiles
const profilesData = [
  {
    id: 1,
    name: "John Doe",
    position: "Software Engineer",
    pastProjects: ["Project A", "Project B", "Project C"],
    expertiseYears: 5,
    technicalSkills: ["JavaScript", "React", "Node.js", "MongoDB"],
  },
  {
    id: 2,
    name: "Jane Smith",
    position: "Data Scientist",
    pastProjects: ["Data Project X", "Data Project Y"],
    expertiseYears: 7,
    technicalSkills: ["Python", "Machine Learning", "Data Visualization"],
  },
  // Add more profiles as needed
];

const useStyles = makeStyles((theme) => ({
  table: {
    minWidth: 650,
  },
  tableHeaderCell: {
    fontWeight: "bold",
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.primary.contrastText,
  },
  addButton: {
    marginRight: theme.spacing(2), // Add margin-right to the button
  },
}));

function ProfilePage() {
  const classes = useStyles();
  const [openForm, setOpenForm] = useState(false); // State to manage form open/close

  const handleOpenForm = () => {
    setOpenForm(true);
  };

  const handleCloseForm = () => {
    setOpenForm(false);
  };

  return (
    <div>
      <h1>Profiles</h1>
      <Button className={classes.addButton} variant="contained" color="primary" onClick={handleOpenForm}>Add New Profile</Button>
      <TableContainer component={Paper} style={{ marginTop: theme.spacing(2) }}> {/* Add margin-top to the table */}
        <Table className={classes.table}>
          <TableHead>
            <TableRow>
              <TableCell className={classes.tableHeaderCell}>Name</TableCell>
              <TableCell className={classes.tableHeaderCell}>Position</TableCell>
              <TableCell className={classes.tableHeaderCell}>Past Projects</TableCell>
              <TableCell className={classes.tableHeaderCell}>Years of Expertise</TableCell>
              <TableCell className={classes.tableHeaderCell}>Technical Skills</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {profilesData.map((profile) => (
              <TableRow key={profile.id}>
                <TableCell>{profile.name}</TableCell>
                <TableCell>{profile.position}</TableCell>
                <TableCell>{profile.pastProjects.join(", ")}</TableCell>
                <TableCell>{profile.expertiseYears}</TableCell>
                <TableCell>{profile.technicalSkills.join(", ")}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <NewProfileForm open={openForm} handleClose={handleCloseForm} /> {/* Render the form component */}
    </div>
  );
}

export default ProfilePage;
