import React, { useState, useEffect } from "react";
import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from "@mui/material";
import { makeStyles, useTheme } from "@mui/styles";
import NewProfileForm from "./NewProfileForm";
import firebase from "firebase/app";
import "firebase/firestore";

const useStyles = makeStyles((theme) => ({
  table: {
    minWidth: 650,
  },
  tableHeaderCell: {
    fontWeight: "bold",
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.primary.contrastText,
  },
  addButton: {
    marginRight: theme.spacing(2),
  },
}));

function ProfilePage() {
  const theme = useTheme();
  const classes = useStyles(theme);
  const [openForm, setOpenForm] = useState(false);
  const [profiles, setProfiles] = useState([]);

  useEffect(() => {
    // Fetch profiles from Firestore when component mounts
    fetchProfiles();
  }, []);

  const fetchProfiles = async () => {
    try {
      const snapshot = await firebase.firestore().collection("profiles").get();
      const fetchedProfiles = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setProfiles(fetchedProfiles);
    } catch (error) {
      console.error("Error fetching profiles:", error);
    }
  };

  const addProfile = async (newProfile) => {
    try {
      await firebase.firestore().collection("profiles").add(newProfile);
      fetchProfiles(); // Refresh profiles after adding a new one
    } catch (error) {
      console.error("Error adding profile:", error);
    }
  };

  const handleOpenForm = () => {
    setOpenForm(true);
  };

  const handleCloseForm = () => {
    setOpenForm(false);
  };

  return (
    <div>
      <h1>Profiles</h1>
      <Button className={classes.addButton} variant="contained" color="primary" onClick={handleOpenForm}>
        Add New Profile
      </Button>
      <TableContainer component={Paper} style={{ marginTop: theme.spacing(2) }}>
        <Table className={classes.table}>
          <TableHead>
            <TableRow>
              <TableCell className={classes.tableHeaderCell}>Name</TableCell>
              <TableCell className={classes.tableHeaderCell}>Position</TableCell>
              <TableCell className={classes.tableHeaderCell}>Past Projects</TableCell>
              <TableCell className={classes.tableHeaderCell}>Years of Expertise</TableCell>
              <TableCell className={classes.tableHeaderCell}>Technical Skills</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {profiles.map((profile) => (
              <TableRow key={profile.id}>
                <TableCell>{profile.name}</TableCell>
                <TableCell>{profile.position}</TableCell>
                <TableCell>{profile.pastProjects.join(", ")}</TableCell>
                <TableCell>{profile.expertiseYears}</TableCell>
                <TableCell>{profile.technicalSkills.join(", ")}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <NewProfileForm open={openForm} handleClose={handleCloseForm} addProfile={addProfile} />
    </div>
  );
}

export default ProfilePage;
